````javascript:deploy1.3.4_combine_update/frontend/script.js
// Cập nhật event listener cho nút Save
document.getElementById('saveScoreButton').addEventListener('click', async function(e) {
    // Ngăn chặn mọi hành vi mặc định
    e.preventDefault();
    e.stopPropagation();

    const videoLink = document.getElementById('videoLink').value.trim();
    const scoreInput = document.getElementById('scoreInput').value;
    const noteInput = document.getElementById('noteInput').value;

    if (!videoLink || !currentCriteria) {
        showNotification('Please select a video and criteria first', 'error');
        return;
