    try {
        const saveResponse = await fetch('http://localhost:3000/save_evaluation', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                video_url: videoLink,
                criteria: currentCriteria,
                score: parseInt(scoreInput),
                note: noteInput
            })
        });

        const result = await saveResponse.json();

        if (saveResponse.ok) {
            // Cập nhật UI
            displayResults(currentCriteria, noteInput, scoreInput);

            // Clear input fields
            document.getElementById('scoreInput').value = '';
            document.getElementById('noteInput').value = '';

            showNotification('Evaluation saved successfully!', 'success');

            // Cập nhật thông tin hiển thị
            const videoId = videoLink.match(/\/d\/(.+?)\//)[1];
            await displayCriteriaRecommendation(currentCriteria, videoId);
        } else {
            throw new Error(result.error || 'Failed to save evaluation');
        }
    } catch (error) {
        console.error('Error:', error);
        showNotification('Error saving evaluation: ' + error.message, 'error');
    }
});

// Thêm event listener để ngăn chặn form submit
document.addEventListener('DOMContentLoaded', function() {
    // Ngăn chặn tất cả các form submit
    document.querySelectorAll('form').forEach(form => {
        form.addEventListener('submit', function(e) {
            e.preventDefault();
            return false;
        });
    });
});
````

