// Function to process and format the video data
function processVideoData(scores) {
    if (!scores.length) return [];

    // Group scores by video URL
    const videoMap = new Map();

    scores.forEach(score => {
        if (!videoMap.has(score.url_video)) {
            videoMap.set(score.url_video, {
                url_video: score.url_video,
                title: extractVideoName(score.url_video), // You'll need to implement this
                criteria: {},
                timestamp: score.timestamp
            });
        }

        const videoData = videoMap.get(score.url_video);
        videoData.criteria[score.criteria] = {
            score: score.score,
            note: score.note
        };
    });

    return Array.from(videoMap.values());
}

// Helper function to extract video name from URL
function extractVideoName(url) {
    // Extract file ID from Google Drive URL
    const fileId = url.match(/\/d\/(.+?)\/view/)?.[1] || 'Unknown Video';
    return `Video ${fileId}`;
}

