  }
};

// Determine environment based on hostname
const getEnvironment = () => {
  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
    return 'development';
  }
  return 'production';
};

export const getConfig = () => {
  const env = getEnvironment();
  return config[env];
};
